const body = $input.first().json.body;
const action = body.action;

if (action === 'rebuild-mappings') {
  return [{ json: { action: 'rebuild-mappings', github_to_clickup: body.github_to_clickup, clickup_to_github: body.clickup_to_github } }];
}

const issue = body.issue || {};
const comment = body.comment || {};

const title = issue.title || '';
const issueBody = issue.body || '';
const commentBody = comment.body || '';

if (title.includes('[sync]') || issueBody.includes('[sync]') || commentBody.includes('[sync]')) {
  return [];
}

const repo = body.repository ? body.repository.full_name : 'unknown';
const repoShort = body.repository ? body.repository.name : 'unknown';

const listMap = {
  'enterprise': '901710789803',
  'siege_utilities': '901710789806',
  'rundeck': '901710789808',
  'ops': '901710789809',
  'portainer': '901710789810'
};

const masterListId = '901710789811';
const repoListId = listMap[repoShort] || null;
if (!repoListId) return [];

const assignee = body.assignee || {};
const currentAssignees = (issue.assignees || []).map(a => a.login);

return [{
  json: {
    action, repo, repoShort, repoListId, masterListId,
    issueNumber: issue.number,
    issueTitle: issue.title,
    issueBody: issue.body || '',
    issueState: issue.state,
    issueUrl: issue.html_url,
    issueLabels: (issue.labels || []).map(l => l.name),
    commentBody: comment.body || '',
    commentUser: comment.user ? comment.user.login : '',
    senderLogin: body.sender ? body.sender.login : '',
    assigneeLogin: assignee.login || '',
    currentAssignees: currentAssignees
  }
}];

const data = $input.first().json;
const staticData = $getWorkflowStaticData('global');
if (!staticData.github_to_clickup) staticData.github_to_clickup = {};
if (!staticData.clickup_to_github) staticData.clickup_to_github = {};

const mappingKey = `${data.repoShort}/${data.issueNumber}`;
if (staticData.github_to_clickup[mappingKey]) {
  return [{ json: { skipped: true, reason: 'already mapped', mappingKey } }];
}

const ghToClickupUser = {
  'dheerajchand': 95317754,
  'steveblackmon': 192189161
};
const assigneeIds = (data.currentAssignees || [])
  .map(login => ghToClickupUser[login])
  .filter(id => id !== undefined);

return [{
  json: {
    ...data, mappingKey,
    taskName: `[${data.repoShort}#${data.issueNumber}] ${data.issueTitle}`,
    taskDescription: `[sync] Synced from GitHub: ${data.issueUrl}\n\n${data.issueBody}`,
    tags: data.issueLabels,
    assigneeIds: assigneeIds
  }
}];

const repoTask = $('Create Repo Task').first().json;
const masterTask = $('Create Master Task').first().json;
const prepData = $('Prepare Create').first().json;

const staticData = $getWorkflowStaticData('global');
if (!staticData.github_to_clickup) staticData.github_to_clickup = {};
if (!staticData.clickup_to_github) staticData.clickup_to_github = {};

const mappingKey = prepData.mappingKey;
staticData.github_to_clickup[mappingKey] = { repo_list: repoTask.id, master_list: masterTask.id };
staticData.clickup_to_github[repoTask.id] = mappingKey;
staticData.clickup_to_github[masterTask.id] = mappingKey;

return [{ json: { success: true, mappingKey, repoTaskId: repoTask.id, masterTaskId: masterTask.id } }];

const data = $input.first().json;
const staticData = $getWorkflowStaticData('global');
const mapping = (staticData.github_to_clickup || {})[`${data.repoShort}/${data.issueNumber}`];
if (!mapping) return [{ json: { skipped: true, reason: 'no mapping' } }];

return [{ json: { repoTaskId: mapping.repo_list, masterTaskId: mapping.master_list, status: 'shipped' } }];

const data = $input.first().json;
const staticData = $getWorkflowStaticData('global');
const mapping = (staticData.github_to_clickup || {})[`${data.repoShort}/${data.issueNumber}`];
if (!mapping) return [{ json: { skipped: true, reason: 'no mapping' } }];

return [{ json: { repoTaskId: mapping.repo_list, masterTaskId: mapping.master_list, status: 'backlog' } }];

const data = $input.first().json;
const staticData = $getWorkflowStaticData('global');
const mapping = (staticData.github_to_clickup || {})[`${data.repoShort}/${data.issueNumber}`];
if (!mapping) return [{ json: { skipped: true, reason: 'no mapping' } }];

const labelToStatus = {
  'status:scoping': 'scoping',
  'status:in-design': 'in design',
  'status:ready': 'ready for development',
  'status:in-progress': 'in development',
  'status:in-review': 'in review',
  'status:testing': 'testing'
};

const statusLabels = data.issueLabels.filter(l => l.startsWith('status:'));
let clickupStatus = 'backlog';
if (statusLabels.length > 0) {
  clickupStatus = labelToStatus[statusLabels[statusLabels.length - 1]] || 'backlog';
}

return [{ json: { repoTaskId: mapping.repo_list, masterTaskId: mapping.master_list, status: clickupStatus } }];

const data = $input.first().json;
const staticData = $getWorkflowStaticData('global');
const mapping = (staticData.github_to_clickup || {})[`${data.repoShort}/${data.issueNumber}`];
if (!mapping) return [{ json: { skipped: true, reason: 'no mapping' } }];

const commentText = `[sync] Synced from GitHub (${data.commentUser}):\n\n${data.commentBody}`;
return [{ json: { repoTaskId: mapping.repo_list, masterTaskId: mapping.master_list, commentText } }];

const data = $input.first().json;
const staticData = $getWorkflowStaticData('global');
const mapping = (staticData.github_to_clickup || {})[`${data.repoShort}/${data.issueNumber}`];
if (!mapping) return [{ json: { skipped: true, reason: 'no mapping' } }];

const ghToClickupUser = {
  'dheerajchand': 95317754,
  'steveblackmon': 192189161
};

const clickupUserId = ghToClickupUser[data.assigneeLogin];
if (!clickupUserId) return [{ json: { skipped: true, reason: 'unknown assignee', login: data.assigneeLogin } }];

let assigneePayload;
if (data.action === 'assigned') {
  assigneePayload = { assignees: { add: [clickupUserId], rem: [] } };
} else {
  assigneePayload = { assignees: { add: [], rem: [clickupUserId] } };
}

return [{
  json: {
    repoTaskId: mapping.repo_list,
    masterTaskId: mapping.master_list,
    assigneePayload: assigneePayload
  }
}];

const data = $input.first().json;
const staticData = $getWorkflowStaticData('global');

if (!data || !data.github_to_clickup || !data.clickup_to_github) {
  return [{ json: { success: false, error: 'Invalid payload: must contain github_to_clickup and clickup_to_github objects' } }];
}

staticData.github_to_clickup = data.github_to_clickup;
staticData.clickup_to_github = data.clickup_to_github;

const ghCount = Object.keys(data.github_to_clickup).length;
const cuCount = Object.keys(data.clickup_to_github).length;

return [{ json: { success: true, github_to_clickup_count: ghCount, clickup_to_github_count: cuCount } }];
